<script setup>
import { ref } from 'vue';

const items = ref([
  { id: 1, name: 'Air Jordan 1 High Red Chenille', description: '$170', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQvcnCu7jqz_r_vswgA34YB3C6b_gUyGGiNpw&s', quantity: 0 },
  { id: 2, name: 'Louis Vuitton Nike Airforce 1 ', description: '$2,750', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR8DMcqc0adDaoqmluLSnK2AL9QvWTb40k11Q&s', quantity: 0 },
  { id: 3, name: 'Vans Sneakers', description: '$50', image: 'data:image/jpeg;base64,', quantity: 0 },
  { id: 4, name: 'Warriors Basketball Jersey', description: '$1050', image: 'data:image/jpeg;base64,', quantity: 0 },
  { id: 5, name: 'Manchester City Jersey', description: '$900', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcToheprmPrloZG5fKeWmX_INBE3LnRuuSrGBA&s', quantity: 0 },
  { id: 6, name: 'Nike Tech Fleese', description: '$2000', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTa4SyaYa_dl5GvIxlUOz1h9cvgwn96VMBktQ&s', quantity: 0 },
]);

const cart = ref([]);

function addToCart(item) {
  const cartItem = cart.value.find(cartItem => cartItem.id === item.id);
  if (cartItem) {
    cartItem.quantity++;
  } else {
    cart.value.push({ ...item, quantity: 1 });
  }
}

function removeFromCart(item) {
  const cartItem = cart.value.find(cartItem => cartItem.id === item.id);
  if (cartItem) {
    if (cartItem.quantity > 1) {
      cartItem.quantity--;
    } else {
      cart.value = cart.value.filter(cartItem => cartItem.id !== item.id);
    }
  }
}

function buyItems() {
  console.log('Buying items:', cart.value);
  // Implement the buying logic here
}

function removeAllItems() {
  cart.value = [];
}
</script>

