            max-width="350"
          >
            <v-card-title>{{ cartItem.name }} ({{ cartItem.quantity }})</v-card-title>
            <v-card-actions>
              <v-btn color="blue" @click="removeFromCart(cartItem)">Remove One</v-btn>
            </v-card-actions>
          </v-card>
        </v-col>
